mapper.update:
  	update review
	set score = #{score}, content = #{content}, update_date = SYSDATE
	where review_num = #{reviewNum}

mapper.select:

  		select *
		from(select rownum as rn, l.*
		    from locker l where rownum <= #{pageNum} * #{amount} and user_code = #{userCode})
		where rn > (#{pageNum} -1) * #{amount}
