mapper.select:

select * 
from(
	select /*+INDEX_DESC(rent_history) */ rownum as rn, r.history_index, r.rent_start, r.rent_end, l.locker_title, l.locker_image
  	from rent_history r, locker l where rownum <= #{pageNum} * #{amount} and r.user_code = #{userCode} 
  										and r.locker_id = l.locker_id)
where rn > (#{pageNum} -1) * #{amount}


  		select * 
	    from(
	       	select /*+INDEX_DESC(rent_history) */ rownum as rn, r.*, m.nick, l.locker_image, l.locker_title
	        from review r, member m, locker l where rownum <= #{pageNum} * #{amount} and m.user_code = l.user_code
	          										and r.locker_id = l.locker_id
                               						and r.user_code = #{userCode}
                               						and r.deleteyn = 'N')
	    where rn > (#{pageNum} -1) * #{amount}

mapper.update:
  	update review
	set score = #{score}, content = #{content}, update_date = SYSDATE
	where review_num = #{reviewNum}
